.quick{
    position: absolute;
    z-index: 99;
    top: 30%;
    right: 0;
    background-color: #fff;
    transition: width .3s;
    transition: background-color .3s;
    cursor: pointer;
    li{transition: .3s;}
    li:hover{
        color: #fff;
        background-color: $mainC;
    }

    &:hover .quick_top{
        li{width: 150px;}
        p{width: 90px;font-size: 16px;}
    }

    .quick_top{
        >li{
            width: 60px;
            overflow: hidden;         

            p{
                float: left;
                width: 0px; height: 60px;
                text-align: center;line-height: 60px;
                
                font-size: 0px;
                transition: width .3s;

                &::after{
                    content: "";
                    display: block;
                    clear: both;
                }
            }
            span{
                display: block;
                float: left;

                width: 60px; height: 60px;
                text-align: center; line-height: 60px;
                font-size: 52px;
            }
        }

    
    }

    .quick_bottom{
        text-align: center;
    }

}
